import React, { useState } from "react";
import styled from "styled-components";
import logo from "../assets/logo.png";
import {
  primaryColorDarker,
  primaryColorLighter,
  primaryColorSuperLight,
} from "./colors";
import { useEffect } from "react";
import Select from "react-select";
import { useNavigate } from "react-router-dom";

const StyledHeader = styled.header`
  width: 80vw;
  margin: 20px auto;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;
const Logo = styled.img`
  width: 100%;
  max-width: 80px;
`;
const LogoWithName = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  /* background-color: ${primaryColorLighter}; */
  border-radius: 1rem;
  box-shadow: 0px 0px 5px 1px #cccccc;
  h1 {
    font-size: 2rem;
    display: flex;
    align-items: center;
    text-transform: uppercase;
    letter-spacing: 1rem;
    color: ${primaryColorDarker};
    font-weight: 900;
  }
`;
const SearchWrapper = styled.div`
  background-color: ${primaryColorSuperLight};
  border-radius: 20px;
  margin-top: 1rem;
  padding: 40px 8px;
  display: flex;
  justify-content: center;
  box-shadow: 0px 0px 5px 1px #cccccc;
  div:first-child {
    width: 100%;
    max-width: 300px;
  }
`;

function Header({ topTenCryptos, loading }) {
  const [value, setValue] = useState(null);
  const navigate = useNavigate();
  const options = topTenCryptos.map((data) => ({
    label: data.ticker.split("/")[0],
    value: data.ticker.split("/")[0],
  }));

  const handleOptionSelect = (selectedOption) => {
    if (selectedOption) {
      const selectedValue = selectedOption.value;
      navigate(`/${selectedValue}`);
    }
  };

  return (
    <StyledHeader>
      {/* ... Your other header content */}
      <SearchWrapper>
        <Select
          searchable
          value={value}
          options={options}
          onChange={(newValue) => {
            setValue(newValue);
            handleOptionSelect(newValue);
          }}
          placeholder={loading ? "Loading..." : "Search"}
          styles={{
            control: (provided) => ({
              ...provided,
              maxWidth: "300px",
              width: "100%",
            }),
          }}
          components={{
            Input: CustomSearchBar,
          }}
        />
      </SearchWrapper>
    </StyledHeader>
  );
}

// Custom input component to enforce character limit
function CustomSearchBar() {
  const maxCharacters = 30;
  const [remainingCharacters, setRemainingCharacters] = useState(maxCharacters);

  function handleInputChange(event) {
    const characters = event.target.value;
    setRemainingCharacters(maxCharacters - characters.length);
  }

  function combinedOnChange(event) {
    handleInputChange(event);
    onChange(event);
  }

  return (
    <SearchBar>
      <input
        onChange={combinedOnChange}
        type="text"
        maxLength={maxCharacters}
        placeholder="Search By Ticker"
        value={value}
      />
      <RemainingCharacters>
        Remaining Characters: {remainingCharacters}
      </RemainingCharacters>
      {remainingCharacters === 0 ? (
        <InputError>Character Limit Reached!</InputError>
      ) : null}
      <Button>
        <SearchIcon fontSize="large" />
      </Button>
    </SearchBar>
  );
}

export default Header;
