import React, { useState } from "react";
import styled from "styled-components";
import logo from "../assets/logo.png";
import {
  primaryColor,
  primaryColorDarker,
  primaryColorLighter,
} from "./colors";
import SearchIcon from "@mui/icons-material/Search";
import { Button } from "@mui/material";

const StyledHeader = styled.header`
  width: 80vw;
  margin: 80px auto;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;
const Logo = styled.img`
  width: 100%;
  max-width: 80px;
`;
const LogoWithName = styled.div`
  display: flex;
  justify-content: center;
  background-color: ${primaryColorLighter};
  border-radius: 1rem;
  box-shadow: -1px 1px 1px 1px #526c7f;
  h1 {
    font-size: 2rem;
    display: flex;
    align-items: center;
    text-transform: uppercase;
    letter-spacing: 1rem;
    color: ${primaryColorDarker};
    font-weight: 900;
  }
`;



function Header() {
  return (
    <StyledHeader>
      <div>
        <LogoWithName className="">
          <figure>
            <Logo src={logo} alt="" />
          </figure>
          <h1>CryptoCurrency Price Tracker</h1>
        </LogoWithName>
      </div>
      <SearchBar>
        <input
          onChange={handleInputChange}
          type="text"
          maxLength={maxCharacters}
          placeholder="Search"
        />
        <RemainingCharacters>
          Remaining Characters: {remaindingCharacters}
        </RemainingCharacters>
        {remaindingCharacters === 0 ? (
          <InputError>Character Limit Reached!</InputError>
        ) : null}
        <Button>
          <SearchIcon fontSize="large" />
        </Button>
      </SearchBar>
    </StyledHeader>
  );
}

export default Header;
