import React from "react";
import { styled } from "styled-components";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";

const CryptoWrapper = styled.div`
  border-radius: 20px;
  height: 60px;
  display: flex;
  align-items: center;
  transition: all 300ms ease;
  img {
    width: 40px;
    height: 40px;
  }
  div {
    width: 25%;
    text-align: center;
  }
  div .MuiSvgIcon-root:hover {
    transition: all 300ms ease-in-out;
    transform: scale(1.3);
  }
  &:hover {
    background-color: #ecd8f4;
  }
`;
const CryptoInfo = styled.div`
  display: flex;
  justify-content: center;
`;

const CryptoNames = styled.div`
  margin-left: 1rem;
  display: flex;
  flex-direction: column;
  div {
    width: 100%;
    text-align: left;
  }
  div:first-child {
    border-bottom: 1px solid #ccc;
  }
`;
const Seperator = styled.hr`
  width: 90%;
  border-color: #ffffff;
`;
const GrayCircle = styled.div`
  max-width: 40px;
  max-height: 40px;
  border-radius: 100%;
  background-color: #bdbdbd;
`;

function Crypto({ fullName, price, ticker, change, logo }) {
  const changeColor = parseFloat(change) < 0 ? "red" : "green";
  return (
    <>
      <CryptoWrapper>
        <CryptoInfo>
          {logo ? <img src={logo} alt="" /> : <GrayCircle></GrayCircle>}
          <CryptoNames>
            {fullName ? <div>{fullName}</div> : <div>fulllname</div>}
            {ticker ? <div>{ticker}</div> : <div>TIC</div>}
          </CryptoNames>
        </CryptoInfo>
        {price ? <div>{price}$</div> : <div>price</div>}
        <div style={{ color: changeColor }}>
          {change !== undefined ? `${change}` : "N/A"}
        </div>
        <div>
          <KeyboardArrowRightIcon fontSize="large" />
        </div>
      </CryptoWrapper>
      <Seperator />
    </>
  );
}

export default Crypto;
