import React, { useState } from "react";
import { styled } from "styled-components";
import { primaryColor, primaryColorLighter } from "./colors";

const SearchBar = styled.div`
  background: #ffffff;
  /* position: relative; */
  display: flex;
  justify-content: center;
  width: 100%;
  display: flex;
  justify-content: center;
  z-index: 1;
  input {
    background: #ffffff;
    width: 100%;
    height: 40px;
    border: none;
    padding-left: 8px;
    font-size: 2rem;
    border-radius: 8px;
  }
  input::placeholder {
    color: ${primaryColorLighter};
  }
  input:focus-visible {
    outline: none;
  }
  .MuiButtonBase-root {
    color: ${primaryColor};
  }
`;

