//   animation: showUp 150ms ease-in-out;
//   @keyframes showUp {
//     0% {
//       transform: translateY(20px);
//     }
//     100% {
//       transform: none;
//     }
//   }
// `;

function CustomSearchBar({ value, onChange, remainingCharacters }) {
  const maxCharacters = 30;

  function handleInputChange(event) {
    const characters = event.target.value;
    const updatedRemainingCharacters = maxCharacters - characters.length;
    onChange(event, updatedRemainingCharacters); // Pass the updated value to the parent
  }

  return (
    <div>
      <input
        onChange={handleInputChange}
        type="text"
        placeholder="Search"
        maxLength={maxCharacters}
        value={value}
      />
    </div>
  );
}

export default CustomSearchBar;
