function CustomSearchBar({ value, onChange, remainingCharacters }) {
  const maxCharacters = 30;

  function handleInputChange(event) {
    remainingCharacters = maxCharacters - value.length;
    onChange(event, remainingCharacters);
  }

  console.log(value.length)

  return (
    <SearchBar>
      <input
        onChange={handleInputChange}
        type="text"
        placeholder="Search"
        maxLength={maxCharacters}
        value={value}
      />
    </SearchBar>
  );
}

export default CustomSearchBar;
