import React, { useState } from "react";
import { styled } from "styled-components";
import SearchIcon from "@mui/icons-material/Search";
import { Button } from "@mui/material";
import { primaryColor, primaryColorLighter } from "./colors";

const SearchBar = styled.div`
  background: #ffffff;
  position: relative;
  display: flex;
  justify-content: center;
  width: 60%;
  display: flex;
  justify-content: center;
  border: 2px solid ${primaryColor};
  border-radius: 7px;
  z-index: 1;
  input {
    background: #ffffff;
    width: 90%;
    height: 40px;
    border: none;
    padding-left: 8px;
    font-size: 2rem;
    border-radius: 8px;
  }
  input::placeholder {
    color: ${primaryColorLighter};
  }
  input:focus-visible {
    outline: none;
  }
  .MuiButtonBase-root {
    color: ${primaryColor};
  }
`;

const RemainingCharacters = styled.span`
  position: absolute;
  right: 0;
  top: -30px;
`;
const InputError = styled.span`
  z-index: -1;
  display: flex;
  justify-content: center;
  position: absolute;
  left: 0;
  top: -35px;
  color: red;
  font-weight: 900;
  border: 3px solid red;
  border-radius: 50px;
  padding: 0px 8px 0;
  white-space: nowrap;
  background-color: white;
  animation: showUp 150ms ease-in-out;
  @keyframes showUp {
    0% {
      transform: translateY(20px);
    }
    100% {
      transform: none;
    }
  }
`;

function CustomSearchBar({ value, onChange }) {
  const maxCharacters = 30;
  const [remaindingCharacters, setRemaindingCharacters] =
    useState(maxCharacters);

  function handleInputChange(event) {
    const characters = event.target.value;
    setRemaindingCharacters(maxCharacters - characters.length);
  }

  return (
    <SearchBar>
      <input
        onChange={handleInputChange}
        type="text"
        maxLength={maxCharacters}
        placeholder="Search By Ticker"
        value={value}
        onChange={onChange}
      />
      <RemainingCharacters>
        Remaining Characters: {remaindingCharacters}
      </RemainingCharacters>
      {remaindingCharacters === 0 ? (
        <InputError>Character Limit Reached!</InputError>
      ) : null}
      <Button>
        <SearchIcon fontSize="large" />
      </Button>
    </SearchBar>
  );
}

export default CustomSearchBar;
