import React, { useState, useEffect } from "react";
import styled from "styled-components";
import logo from "../assets/logo.png";
import {
  primaryColorDarker,
  primaryColorLighter,
  primaryColorSuperLight,
} from "./colors";
import CustomSearchBar from "./CustomSearchBar";

const StyledHeader = styled.header`
  width: 80vw;
  margin: 20px auto;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;
const Logo = styled.img`
  width: 100%;
  max-width: 80px;
`;
const LogoWithName = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 1rem;
  box-shadow: 0px 0px 5px 1px #cccccc;
  h1 {
    font-size: 2rem;
    display: flex;
    align-items: center;
    text-transform: uppercase;
    letter-spacing: 1rem;
    color: ${primaryColorDarker};
    font-weight: 900;
  }
`;
const SearchWrapper = styled.div`
  background-color: ${primaryColorSuperLight};
  border-radius: 20px;
  margin-top: 1rem;
  padding: 40px 0;
  display: flex;
  justify-content: center;
  box-shadow: 0px 0px 5px 1px #cccccc;
`;

function Header() {
  const [searchText, setSearchText] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (searchText) {
      // Replace 'YOUR_API_KEY' with your Binance API key
      const apiKey = "YOUR_API_KEY";

      // Define the Binance API endpoint
      const endpoint = `https://api.binance.com/api/v3/ticker/price?symbol=${searchText.toUpperCase()}USDT`;

      // Set up Axios request headers with your API key
      const headers = {
        "X-MBX-APIKEY": apiKey,
      };

      // Make the API request
      setLoading(true);
      axios
        .get(endpoint, { headers })
        .then((response) => {
          setSearchResults([response.data]);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching data from Binance API:", error);
          setLoading(false);
        });
    } else {
      // Clear the search results when there's no input
      setSearchResults([]);
    }
  }, [searchText]);

  return (
    <StyledHeader>
      <div>
        <LogoWithName className="">
          <figure>
            <Logo src={logo} alt="" />
          </figure>
          <h1>
            CryptoCurrency <br /> Price Tracker
          </h1>
        </LogoWithName>
      </div>
      <SearchWrapper>
        <CustomSearchBar
          value={searchText}
          onChange={(ev) => setSearchText(ev.target.value)}
        />
        {loading && <p>Loading...</p>}
        {searchResults.length > 0 && (
          <div>
            {searchResults.map((result) => (
              <p key={result.symbol}>
                {result.symbol}: ${parseFloat(result.price).toFixed(2)}
              </p>
            ))}
          </div>
        )}
      </SearchWrapper>
    </StyledHeader>
  );
}

export default Header;
