import React, { useState } from "react";
import styled from "styled-components";
import logo from "../assets/logo.png";
import {
  primaryColorDarker,
  primaryColorLighter,
  primaryColorSuperLight,
} from "./colors";
import CustomSearchBar from "./CustomSearchBar";
import { useEffect } from "react";
import Select from "react-select";
import { useNavigate } from "react-router-dom";

const StyledHeader = styled.header`
  width: 80vw;
  margin: 20px auto;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;
const Logo = styled.img`
  width: 100%;
  max-width: 80px;
`;
const LogoWithName = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 1rem;
  box-shadow: 0px 0px 5px 1px #cccccc;
  h1 {
    font-size: 2rem;
    display: flex;
    align-items: center;
    text-transform: uppercase;
    letter-spacing: 1rem;
    color: ${primaryColorDarker};
    font-weight: 900;
  }
`;
const SearchWrapper = styled.div`
  position: relative;
  background-color: ${primaryColorSuperLight};
  border-radius: 20px;
  margin-top: 1rem;
  padding: 40px 8px;
  display: flex;
  justify-content: center;
  box-shadow: 0px 0px 5px 1px #cccccc;
  div:first-child {
    width: 100%;
    max-width: 300px;
  }
`;
//////////////////////
const RemainingCharacters = styled.span`
  position: absolute;
  right: 0;
  top: -39px;
  z-index: 0;
`;
const InputError = styled.span`
  z-index: 0;
  display: flex;
  justify-content: center;
  position: absolute;
  left: 0;
  top: -35px;
  color: red;
  font-weight: 900;
  border: 3px solid red;
  border-radius: 50px;
  padding: 0px 8px 0;
  white-space: nowrap;
  background-color: white;
  animation: showUp 150ms ease-in-out;
  @keyframes showUp {
    0% {
      transform: translateY(20px);
    }
    100% {
      transform: none;
    }
  }
`;

function Header({ topTenCryptos }) {
  const [value, setValue] = useState("");
  const navigate = useNavigate();
  const options = topTenCryptos.map((data) => ({
    label: data.ticker.split("/")[0],
    value: data.ticker.split("/")[0],
  }));
  // State for remaining characters
  const [remainingCharacters, setRemainingCharacters] = useState(30);

  const handleOptionSelect = (selectedOption) => {
    if (selectedOption) {
      const selectedValue = selectedOption.value;
      navigate(`/${selectedValue}`);
    }
  };

  const handleInputChange = (newValue) => {
    setValue(newValue);
  };
  console.log(value.length);

  return (
    <StyledHeader>
      <div>
        <LogoWithName className="">
          <figure>
            <Logo src={logo} alt="" />
          </figure>
          <h1>
            CryptoCurrency <br /> Price Tracker
          </h1>
        </LogoWithName>
      </div>
      <SearchWrapper>
        <RemainingCharacters>
          Remaining Characters: {30 - value.length}
        </RemainingCharacters>
        {value.length === 30 ? (
          <InputError>Character Limit Reached!</InputError>
        ) : null}
        <Select
          searchable
          value={value}
          options={options}
          onChange={(newValue) => {
            handleInputChange(newValue);
            handleOptionSelect(newValue);
          }}
          placeholder=""
          styles={{
            control: (provided) => ({
              ...provided,
              maxWidth: "300px",
              width: "100%",
            }),
          }}
          components={{
            Input: (props) => (
              <CustomInputComponent
                {...props}
                remainingCharacters={remainingCharacters}
              />
            ),
          }}
        />
      </SearchWrapper>
    </StyledHeader>
  );
}

function CustomInputComponent({ value, onChange, remainingCharacters }) {
  return (
    <CustomSearchBar
      value={value}
      onChange={onChange}
      remainingCharacters={remainingCharacters}
    />
  );
}

export default Header;
