import React, { useState } from "react";
import styled from "styled-components";
import logo from "../assets/logo.png";
import {
  primaryColor,
  primaryColorDarker,
  primaryColorLighter,
} from "./colors";
import SearchIcon from "@mui/icons-material/Search";
import { Button } from "@mui/material";

const StyledHeader = styled.header`
  width: 80vw;
  margin: 80px auto;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;
const Logo = styled.img`
  width: 100%;
  max-width: 80px;
`;
const LogoWithName = styled.div`
  display: flex;
  justify-content: center;
  background-color: ${primaryColorLighter};
  border-radius: 1rem;
  box-shadow: -1px 1px 1px 1px #526c7f;
  h1 {
    font-size: 2rem;
    display: flex;
    align-items: center;
    text-transform: uppercase;
    letter-spacing: 1rem;
    color: ${primaryColorDarker};
    font-weight: 900;
  }
`;

const SearchBar = styled.div`
  position: relative;
  margin: 40px auto 0;
  width: 60%;
  display: flex;
  justify-content: center;
  border: 2px solid black;
  border-radius: 7px;
  input {
    width: 90%;
    height: 40px;
    border: none;
    margin: 0px 8px;
    font-size: 2rem;
  }
  input:focus-visible {
    outline: none;
  }
  .MuiButtonBase-root {
    color: black;
  }
`;
const RemainingCharacters = styled.span`
  position: absolute;
  left: 0;
  bottom: -30px;
`;

function Header() {
  const [characters, setCharacters] = useState(0);

  function handleInputChange() {
    console.log(event);
  }

  return (
    <StyledHeader>
      <div>
        <LogoWithName className="">
          <figure>
            <Logo src={logo} alt="" />
          </figure>
          <h1>CryptoCurrency Price Tracker</h1>
        </LogoWithName>
      </div>
      <SearchBar>
        <input
          onChange={handleInputChange}
          type="text"
          maxLength="30"
        />
        <RemainingCharacters>Remaining Characters: 30</RemainingCharacters>
        <Button>
          <SearchIcon fontSize="large" />
        </Button>
      </SearchBar>
    </StyledHeader>
  );
}

export default Header;
