import React from "react";
import styled from "styled-components";
import logo from "../assets/logo.png";

const StyledHeader = styled.header`
  width: 80vw;
  margin: 80px auto;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;
const Logo = styled.img`
  width: 100%;
  max-width: 80px;
`;
const LogoWithName = styled.div`
  display: flex;
  background-color: #90c1e4;
  border-radius: 1rem;
  box-shadow: -1px 1px 1px 1px #526c7f;
  h1 {
    font-size: 2rem;
    display: flex;
    align-items: center;
    text-transform: uppercase;
    letter-spacing: 1rem;
    color: #143750;
    font-weight: 400;
  }
`;

const SearchBar = styled.div`
    margin-top: 40px;
    display: flex;
    justify-content: center;
    input {
        width: 60%;
        height: 40px;
        border-radius: 3px;
        padding: 0 30px;
        outline: none;
        border: none;
    }
`;

function Header() {
  return (
    <StyledHeader>
      <div>
        <LogoWithName className="">
          <figure>
            <Logo src={logo} alt="" />
          </figure>
          <h1>CryptoCurrency Price Tracker</h1>
        </LogoWithName>
      </div>
      <SearchBar>
        <input type="text" />
      </SearchBar>
    </StyledHeader>
  );
}

export default Header;
